SET NOCOUNT ON; select top 10 * from C_REL_PARTY_DEALER as C_REL_PARTY_DEALER_NON_LEGAL_SUB_ORG where exists(select 1 from C_B_PARTY where TYPE in('P','O') and COUNTRY_CODE in('NO', 'EE', 'LV', 'LT') and HUB_STATE_IND=1 and C_REL_PARTY_DEALER_NON_LEGAL_SUB_ORG.PARTY_ID=C_B_PARTY.ROWID_OBJECT) and NON_LEGAL_SUBORG_ID is not null ORDER BY CAST(ROWID_OBJECT AS INT)